import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './map.css';
import { User, DollarSign, Zap } from 'lucide-react'; 

const StatusBar = () => {
    const [status, setStatus] = useState({
        money: 1250,
        level: 12,
        username: "MapExplorer99"
    });

    return (
        <div className="flex justify-between items-center p-3 sm:p-4 bg-white shadow-xl rounded-xl mb-4 md:mb-6 border-b-4 border-blue-500">

            <div className="flex items-center space-x-2 text-lg sm:text-xl font-bold text-gray-800">
                <DollarSign className="w-5 h-5 sm:w-6 sm:h-6 text-green-500" />
                <span className="text-green-600">${status.money.toLocaleString()}</span>
            </div>

            <div className="flex items-center space-x-2 bg-blue-500 text-white px-4 py-1.5 rounded-full shadow-md transform hover:scale-105 transition duration-300">
                <Zap className="w-4 h-4 sm:w-5 sm:h-5" />
                <span className="text-sm sm:text-base font-extrabold">Level {status.level}</span>
            </div>

            <div className="flex items-center space-x-3">
                <span className="hidden sm:inline text-base font-semibold text-gray-700">
                    {status.username}
                </span>
                <div className="w-10 h-10 bg-indigo-500 rounded-full flex items-center justify-center shadow-inner ring-2 ring-indigo-300">
                    <User className="w-5 h-5 text-white" />
                </div>
            </div>
        </div>
    );
};

export default StatusBar;
