import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix default icon paths for Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

function Map() {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const [pendingLatLng, setPendingLatLng] = useState(null);

  useEffect(() => {
    if (mapContainerRef.current && !mapInstanceRef.current) {
      // Initialize map
      const map = L.map(mapContainerRef.current).setView([51.505, -0.09], 13);
      mapInstanceRef.current = map;

      // Add tile layer
      L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
        attribution: 'Â© OpenStreetMap contributors',
      }).addTo(map);

      // --- Geolocation setup ---
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (pos) => {
            const { latitude, longitude } = pos.coords;
            // Wait until map is ready to avoid Leaflet DOM errors
            map.whenReady(() => {
              setTimeout(() => {
                map.setView([latitude, longitude], 15);

                // Add marker for current location
                L.marker([latitude, longitude])
                  .addTo(map)
                  .bindPopup('ðŸ“ You are here')
                  .openPopup();
              }, 100);
            });
          },
          (err) => {
            console.warn('Geolocation error:', err.message);
          },
          { enableHighAccuracy: true }
        );
      } else {
        console.warn('Geolocation not supported by this browser.');
      }

      // --- Context menu (right-click) handler ---
      map.on('contextmenu', function (e) {
        L.DomEvent.preventDefault(e);
        setPendingLatLng(e.latlng);
      });

      // Cleanup when component unmounts
      return () => {
        map.remove();
        mapInstanceRef.current = null;
      };
    }
  }, []);

  const handleConfirm = () => {
    if (pendingLatLng && mapInstanceRef.current) {
      L.marker(pendingLatLng).addTo(mapInstanceRef.current);
    }
    setPendingLatLng(null);
  };

  const handleCancel = () => {
    setPendingLatLng(null);
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#f4f7f9', minHeight: '100vh' }}>
      <div
        ref={mapContainerRef}
        id="map"
        style={{
          height: '70vh',
          width: '100%',
          borderRadius: '8px',
          boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
          position: 'relative',
        }}
      />

      {pendingLatLng && (
        <div
          style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            background: 'white',
            padding: '20px 30px',
            borderRadius: '12px',
            boxShadow: '0 4px 16px rgba(0,0,0,0.2)',
            zIndex: 1000,
            textAlign: 'center',
          }}
        >
          <p style={{ fontSize: '16px', marginBottom: '15px' }}>
            Add marker at<br />
            <strong>
              {pendingLatLng.lat.toFixed(5)}, {pendingLatLng.lng.toFixed(5)}
            </strong>
            ?
          </p>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
            <button
              onClick={handleConfirm}
              style={{
                background: '#007bff',
                color: 'white',
                border: 'none',
                padding: '8px 14px',
                borderRadius: '6px',
                cursor: 'pointer',
              }}
            >
              Confirm
            </button>
            <button
              onClick={handleCancel}
              style={{
                background: '#e0e0e0',
                border: 'none',
                padding: '8px 14px',
                borderRadius: '6px',
                cursor: 'pointer',
              }}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Map;
