import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

function Map() {
    const mapContainerRef = useRef(null);
    const mapInstanceRef = useRef(null);
    const [pendingLatLng, setPendingLatLng] = useState(null);
    const [currentLocation, setCurrentLocation] = useState(null); // New state

    // 1. Initialize Map
    useEffect(() => {
        if (mapContainerRef.current && !mapInstanceRef.current) {
            // Initial view: London (or any default)
            const map = L.map(mapContainerRef.current).setView([51.505, -0.09], 13);
            mapInstanceRef.current = map;

            L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
                maxZoom: 19,
                attribution: '© OpenStreetMap contributors',
            }).addTo(map);

            map.on('contextmenu', function (e) {
                L.DomEvent.preventDefault(e);
                setPendingLatLng(e.latlng);
            });

            // Clean up
            return () => {
                map.remove();
                mapInstanceRef.current = null;
            };
        }
    }, []);

    // 2. Locate User and Add Marker
    useEffect(() => {
        if (mapInstanceRef.current && !currentLocation) {
            const map = mapInstanceRef.current;

            // Use Leaflet's built-in location function
            map.locate({ setView: true, maxZoom: 16 });

            function onLocationFound(e) {
                const radius = e.accuracy;
                const latlng = e.latlng;
                setCurrentLocation(latlng);

                // Add marker
                L.marker(latlng).addTo(map)
                    .bindPopup("You are here (within " + radius.toFixed(0) + " meters)").openPopup();

                // Add circle to show accuracy
                L.circle(latlng, radius).addTo(map);
            }

            function onLocationError(e) {
                console.error("Location access denied or failed: " + e.message);
                // Optional: Alert the user or set a default location
            }

            map.on('locationfound', onLocationFound);
            map.on('locationerror', onLocationError);

            // Clean up location listeners
            return () => {
                map.off('locationfound', onLocationFound);
                map.off('locationerror', onLocationError);
            };
        }
    }, [currentLocation]); // Re-run if map is loaded and we haven't found the location yet

    // ... (Your existing handleConfirm and handleCancel functions)

    const handleConfirm = () => {
        if (pendingLatLng && mapInstanceRef.current) {
            L.marker(pendingLatLng).addTo(mapInstanceRef.current);
        }
        setPendingLatLng(null);
    };

    const handleCancel = () => {
        setPendingLatLng(null);
    };

    // ... (Your existing return statement)

    return (
        <div style={{ padding: '20px', backgroundColor: '#f4f7f9', minHeight: '100vh' }}>
        <div
            ref={mapContainerRef}
            id="map"
            style={{
            height: '70vh',
            width: '100%',
            borderRadius: '8px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            position: 'relative',
            }}
        />

        {pendingLatLng && (
            <div
            style={{
                position: 'fixed',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                background: 'white',
                padding: '20px 30px',
                borderRadius: '12px',
                boxShadow: '0 4px 16px rgba(0,0,0,0.2)',
                zIndex: 1000,
                textAlign: 'center',
            }}
            >
            <p style={{ fontSize: '16px', marginBottom: '15px' }}>
                Add marker at<br />
                <strong>
                {pendingLatLng.lat.toFixed(5)}, {pendingLatLng.lng.toFixed(5)}
                </strong>
                ?
            </p>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
                <button
                onClick={handleConfirm}
                style={{
                    background: '#007bff',
                    color: 'white',
                    border: 'none',
                    padding: '8px 14px',
                    borderRadius: '6px',
                    cursor: 'pointer',
                }}
                >
                Confirm
                </button>
                <button
                onClick={handleCancel}
                style={{
                    background: '#e0e0e0',
                    border: 'none',
                    padding: '8px 14px',
                    borderRadius: '6px',
                    cursor: 'pointer',
                }}
                >
                Cancel
                </button>
            </div>
            </div>
        )}
        </div>
    );
}

export default Map;
