import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function Map() {
    const mapContainerRef = useRef(null);
    const mapInstanceRef = useRef(null);

    useEffect(() => {
        if (mapContainerRef.current && !mapInstanceRef.current) {
            // Initialize the map
            const map = L.map(mapContainerRef.current).setView([51.505, -0.09], 13);
            mapInstanceRef.current = map;

            // Add the tile layer
            L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
                maxZoom: 19,
                attribution: 'Â© OpenStreetMap contributors'
            }).addTo(map);

            // ðŸŒŸ RIGHT-CLICK (contextmenu) LISTENER ðŸŒŸ
            map.on('contextmenu', function(e){
                L.DomEvent.preventDefault(e); 
                const latlng = e.latlng;

                // Popup HTML content with a unique button ID
                const popupContent = `
                    <div>
                        <p>Add a new location here?</p>
                        <button id="add-marker-btn" 
                                style="padding: 5px 10px; cursor: pointer; background-color: #0078A8; color: white; border: none; border-radius: 4px;">
                            Confirm Location
                        </button>
                    </div>
                `;

                // Create and open the temporary popup
                const popup = L.popup()
                    .setLatLng(latlng)
                    .setContent(popupContent)
                    .openOn(map);

                // Attach event handler once the popup is open and its HTML is in the DOM
                popup.on('open', function() {
                    const button = document.getElementById('add-marker-btn');

                    if (button) {
                        button.onclick = function() {
                            // 1. Close the introductory popup
                            map.closePopup(popup);

                            // 2. Add the permanent marker
                            L.marker(latlng).addTo(map)
                                .bindPopup("New Marker Added!")
                                .openPopup();
                        };
                    }
                });
            });
            // ðŸŒŸ END OF LISTENER ðŸŒŸ

            // Cleanup function
            return () => {
                map.remove();
                mapInstanceRef.current = null;
            };
        }
    }, []);

    // The map container div
    return (
        <div 
            ref={mapContainerRef} 
            id="map" 
            style={{ height: '500px', width: '100%' }}
        />
    );
}

export default Map;
