import React, { useState } from 'react';
// Removed 'lucide-react' imports to eliminate the compilation error. 
// Icons are replaced with inline SVGs below.

/**
 * Renders a stylish, responsive status bar displaying game/user metrics.
 * Note: Uses inline SVG icons instead of lucide-react to ensure compilation 
 * in single-file environments.
 */
const StatusBar = () => {
    // State for the Status Bar Data
    const [status, setStatus] = useState({
        money: 1250,
        level: 12,
        username: "MapExplorer99"
    });

    // Inline SVG definitions (replacing lucide-react)
    const DollarSignIcon = (props) => (
        // Restored correct viewBox for proper icon rendering
        <svg {...props} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1 1" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`${props.className || 'w-6 h-6'}`}>
            <path d="M12 1v22M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"/>
        </svg>
    );

    const ZapIcon = (props) => (
        // Restored correct viewBox for proper icon rendering
        <svg {...props} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1 1" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`${props.className || 'w-6 h-6'}`}>
            <path d="M10.824 16.549L17 8H9L12 1v6L7 16h6l-2.176 5.549zM19 13l-4 5"/>
        </svg>
    );

    const UserIcon = (props) => (
        // Restored correct viewBox for proper icon rendering
        <svg {...props} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1 1" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`${props.className || 'w-6 h-6'}`}>
            <circle cx="12" cy="7" r="4"/>
            <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
        </svg>
    );

    // Reduced vertical padding (py-2) for mobile responsiveness 
    return (
        <div className="flex justify-between items-center py-1 px-1 sm:p-4 bg-white shadow-xl rounded-xl mb-1 md:mb-1 border-b-1 border-blue-1">

            {/* Left: Money */}
            <div className="flex items-center space-x-1 text-base sm:text-s font-bold text-gray-800">
                {/* Smaller icon size for mobile (w-4 h-4) */}
                <DollarSignIcon className="w-1 h-1 sm:w-1 sm:h-1 text-green-500" />
                <span className="text-green-600">${status.money.toLocaleString()}</span>
            </div>

            {/* Middle: Level */}
            <div className="flex items-center space-x-2 bg-blue-500 text-white px-3 py-1 rounded-full shadow-md transform hover:scale-105 transition duration-300">
                {/* Smaller icon size for mobile (w-4 h-4) */}
                <ZapIcon className="w-1 h-1 sm:w-1 sm:h-1" />
                {/* Smaller text size for mobile */}
                <span className="text-xs sm:text-base font-extrabold">Level {status.level}</span>
            </div>

            <div className="flex items-center space-x-2">
                <span className="hidden sm:inline text-sm font-semibold text-gray-700">
                    {status.username}
                </span>
                <div className="w-8 h-8 bg-indigo-500 rounded-full flex items-center justify-center shadow-inner ring-2 ring-indigo-300">
                    <UserIcon className="w-4 h-4 text-white" />
                </div>
            </div>
        </div>
    );
};

export default StatusBar;
