import React, { useState } from 'react';
// Removed 'lucide-react' imports to eliminate the compilation error. 
// Icons are replaced with inline SVGs below.

/**
 * Renders a stylish, responsive status bar displaying game/user metrics.
 * Note: Uses inline SVG icons instead of lucide-react to ensure compilation 
 * in single-file environments.
 */
const StatusBar = () => {
    // State for the Status Bar Data
    const [status, setStatus] = useState({
        money: 1250,
        level: 12,
        username: "MapExplorer99"
    });

    // Reduced vertical padding (py-2) for mobile responsiveness 
    return (
        <div className="flex justify-between items-center py-2 px-3 sm:p-4 bg-white shadow-xl rounded-xl mb-4 md:mb-6 border-b-4 border-blue-500">

            {/* Left: Money */}
            <div className="flex items-center space-x-2 text-base sm:text-xl font-bold text-gray-800">
                {/* Smaller icon size for mobile (w-4 h-4) */}
                <DollarSignIcon className="w-4 h-4 sm:w-6 sm:h-6 text-green-500" />
                <span className="text-green-600">${status.money.toLocaleString()}</span>
            </div>

            {/* Middle: Level */}
            <div className="flex items-center space-x-2 bg-blue-500 text-white px-3 py-1 rounded-full shadow-md transform hover:scale-105 transition duration-300">
                {/* Smaller icon size for mobile (w-4 h-4) */}
                <ZapIcon className="w-4 h-4 sm:w-5 sm:h-5" />
                {/* Smaller text size for mobile */}
                <span className="text-xs sm:text-base font-extrabold">Level {status.level}</span>
            </div>

            <div className="flex items-center space-x-2">
                <span className="hidden sm:inline text-sm font-semibold text-gray-700">
                    {status.username}
                </span>
                <div className="w-8 h-8 bg-indigo-500 rounded-full flex items-center justify-center shadow-inner ring-2 ring-indigo-300">
                    <UserIcon className="w-4 h-4 text-white" />
                </div>
            </div>
        </div>
    );
};

export default StatusBar;
