import React, { useState } from 'react';

const StatusBar = () => {
    const [status, setStatus] = useState({
        money: 1250,
        level: 12,
        username: "MapExplorer99"
    });

    const DollarSignIcon = (props) => (
        <svg {...props} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1 1" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`${props.className || 'w-6 h-6'}`}>
            <path d="M12 1v22M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"/>
        </svg>
    );

    const ZapIcon = (props) => (
        <svg {...props} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1 1" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`${props.className || 'w-6 h-6'}`}>
            <path d="M10.824 16.549L17 8H9L12 1v6L7 16h6l-2.176 5.549zM19 13l-4 5"/>
        </svg>
    );

    const UserIcon = (props) => (
        <svg {...props} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1 1" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`${props.className || 'w-6 h-6'}`}>
            <circle cx="12" cy="7" r="4"/>
            <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
        </svg>
    );

    return (
        <div className="flex justify-between items-center p-3 sm:p-4 bg-white shadow-xl rounded-xl mb-4 md:mb-6 border-b-4 border-blue-500, height: 30px">

            <div className="flex items-center space-x-2 text-lg sm:text-xl font-bold text-gray-800">
                <DollarSignIcon className="w-1 h-1 sm:w-1 sm:h-1 text-green-500" />
                <span className="text-green-600">${status.money.toLocaleString()}</span>
            </div>

            <div className="flex items-center space-x-2 bg-blue-500 text-white px-4 py-1.5 rounded-full shadow-md transform hover:scale-105 transition duration-300">
                <ZapIcon className="w-1 h-1 sm:w-1 sm:h-1" />
                <span className="text-sm sm:text-base font-extrabold">Level {status.level}</span>
            </div>

            <div className="flex items-center space-x-3">
                <span className="hidden sm:inline text-base font-semibold text-gray-700">
                    {status.username}
                </span>
                <div className="w-1 h-1 bg-indigo-500 rounded-full flex items-center justify-center shadow-inner ring-2 ring-indigo-300">
                    <UserIcon className="w-1 h-1 text-white" />
                </div>
            </div>
        </div>
    );
};

export default StatusBar;
