import React, { useEffect, useRef, useState } from 'react';
// import L from 'leaflet'; // REMOVED: Caused compilation error
// import 'leaflet/dist/leaflet.css'; // REMOVED: Caused compilation error

// Note: We rely on the global 'L' object being available via an external CDN.
// The code assumes Leaflet JS and CSS are loaded in the environment.

// Fix for default marker icons due to bundler issues
// We assume L is globally available here.
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

const initialCoords = [51.505, -0.09]; // Default: London

function MapApp() {
    const mapContainerRef = useRef(null);
    const mapInstanceRef = useRef(null);
    const [pendingLatLng, setPendingLatLng] = useState(null);
    const [currentLocation, setCurrentLocation] = useState(null);
    const [statusMessage, setStatusMessage] = useState('Right-click the map to add a marker or click Locate Me.');

    // --- EFFECT 1: Initialize Map ---
    useEffect(() => {
        // We rely on the global 'L' object being available here.
        if (mapContainerRef.current && !mapInstanceRef.current && typeof L !== 'undefined') {
            const map = L.map(mapContainerRef.current).setView(initialCoords, 13);
            mapInstanceRef.current = map;

            L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
                maxZoom: 19,
                attribution: '© OpenStreetMap contributors',
            }).addTo(map);

            // Context menu listener for manual marker placement
            map.on('contextmenu', function (e) {
                L.DomEvent.preventDefault(e);
                setPendingLatLng(e.latlng);
            });

            // Clean up map instance when component unmounts
            return () => {
                map.remove();
                mapInstanceRef.current = null;
            };
        }
    }, []);

    // --- EFFECT 2: Locate User and Add Marker (with Unmount Safety) ---
    useEffect(() => {
        if (mapInstanceRef.current) {
            const map = mapInstanceRef.current;
            let mounted = true; // Flag to prevent updating state/map after unmount

            const onLocationFound = (e) => {
                if (!mounted) return; // Crucial check for cleanup safety

                const { latlng, accuracy } = e;

                // Only proceed if we haven't already marked the location
                if (!currentLocation) {
                    setCurrentLocation(latlng);
                    setStatusMessage(`Location found! Accuracy: ${accuracy.toFixed(0)}m.`);

                    // Add marker for current location
                    L.marker(latlng, {
                        icon: L.divIcon({
                            className: 'current-location-icon',
                            html: '<div style="background-color: #ef4444; width: 16px; height: 16px; border-radius: 50%; border: 3px solid white; box-shadow: 0 0 5px rgba(0,0,0,0.5);"></div>',
                            iconSize: [16, 16],
                        })
                    }).addTo(map)
                    .bindPopup("You are here (within " + accuracy.toFixed(0) + " meters)").openPopup();

                    // Add circle to show accuracy
                    L.circle(latlng, accuracy, { color: '#ef4444', weight: 1, fillOpacity: 0.1 }).addTo(map);
                }
            };

            const onLocationError = (e) => {
                if (!mounted) return; // Crucial check for cleanup safety
                setStatusMessage(`Geolocation failed: ${e.message}. Using default map view.`);
                console.error("Geolocation Error:", e.message);
            };

            map.on('locationfound', onLocationFound);
            map.on('locationerror', onLocationError);

            // Cleanup function for this effect
            return () => {
                mounted = false; // Mark as unmounted
                map.off('locationfound', onLocationFound);
                map.off('locationerror', onLocationError);
            };
        }
    }, [currentLocation]); 

    // --- Handlers and Helper Functions ---

    const handleConfirm = () => {
        if (pendingLatLng && mapInstanceRef.current) {
            L.marker(pendingLatLng).addTo(mapInstanceRef.current);
            setStatusMessage(`New marker added at ${pendingLatLng.lat.toFixed(5)}, ${pendingLatLng.lng.toFixed(5)}.`);
        }
        setPendingLatLng(null);
    };

    const handleCancel = () => {
        setPendingLatLng(null);
        setStatusMessage('Marker placement cancelled.');
    };

    const handleLocateMe = () => {
        if (mapInstanceRef.current) {
            setStatusMessage('Attempting to locate you...');
            // This triggers the 'locationfound' or 'locationerror' event handled by useEffect 2
            mapInstanceRef.current.locate({ setView: true, maxZoom: 16 });
        }
    };

    // --- Render Component ---

    return (
        <div className="p-4 sm:p-6 bg-gray-50 min-h-screen font-sans">
            <style>
                {`
                /* Leaflet CSS loaded via CDN */
                @import url('https://unpkg.com/leaflet@1.9.4/dist/leaflet.css');
                @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
                .font-sans { font-family: 'Inter', sans-serif; }
                .leaflet-container { z-index: 1; }
                .popup-modal { z-index: 1000; }
                .current-location-icon { 
                    border: none !important;
                    background: transparent !important;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }
                `}
            </style>

            <div className="max-w-4xl mx-auto">
                <h1 className="text-3xl font-bold text-gray-800 mb-2 flex items-center">
                    <MapPin className="mr-2 text-indigo-600" size={28} />
                    Interactive Map Explorer
                </h1>
                <p className="text-sm text-gray-500 mb-4">{statusMessage}</p>

                {/* Controls */}
                <div className="mb-4 flex justify-end">
                    <button
                        onClick={handleLocateMe}
                        className="flex items-center px-4 py-2 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition duration-150 ease-in-out"
                        title="Find my current location"
                    >
                        <LocateFixed size={20} className="mr-2" />
                        Locate Me
                    </button>
                </div>

                {/* Map Container */}
                <div
                    ref={mapContainerRef}
                    id="map"
                    className="h-[70vh] w-full rounded-xl shadow-2xl border-2 border-gray-200"
                    style={{ position: 'relative' }}
                />

                {/* Confirmation Modal (for right-click) */}
                {pendingLatLng && (
                    <div className="popup-modal fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
                        <div className="bg-white p-6 sm:p-8 rounded-xl shadow-2xl max-w-sm w-full text-center">
                            <h2 className="text-xl font-semibold mb-3 text-gray-800">Confirm Marker Placement</h2>
                            <p className="text-sm text-gray-600 mb-5">
                                Add marker at:
                                <br />
                                <strong className="text-indigo-600">
                                    {pendingLatLng.lat.toFixed(5)}, {pendingLatLng.lng.toFixed(5)}
                                </strong>
                            </p>
                            <div className="flex justify-center space-x-3">
                                <button
                                    onClick={handleConfirm}
                                    className="flex items-center px-4 py-2 bg-emerald-500 text-white font-medium rounded-lg hover:bg-emerald-600 transition duration-150"
                                >
                                    <Check size={18} className="mr-1" /> Confirm
                                </button>
                                <button
                                    onClick={handleCancel}
                                    className="flex items-center px-4 py-2 bg-gray-200 text-gray-700 font-medium rounded-lg hover:bg-gray-300 transition duration-150"
                                >
                                    <X size={18} className="mr-1" /> Cancel
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default MapApp;
