import React, { useEffect, useRef } from 'react';
import L from 'leaflet'; // Assuming you have leaflet installed and imported
import 'leaflet/dist/leaflet.css'; // Don't forget to import the CSS

function Map() {
    // 1. Use useRef to get a reference to the map container DOM element
    const mapContainerRef = useRef(null);

    // 2. Use useRef to keep the map instance across renders
    const mapInstanceRef = useRef(null);

    useEffect(() => {
        // Ensure the map container is available and the map hasn't been initialized yet
        if (mapContainerRef.current && !mapInstanceRef.current) {
            // 3. Initialize the map using the ref's current value (the DOM element)
            const map = L.map(mapContainerRef.current).setView([51.505, -0.09], 13); // Example view: London

            // Save the map instance to the ref
            mapInstanceRef.current = map;

            // 4. Add the tile layer
            L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
                maxZoom: 19,
                attribution: '© OpenStreetMap contributors' // Attribution is important!
            }).addTo(map);

            // Optional: Add a marker
            L.marker([51.5, -0.09]).addTo(map)
                .bindPopup('A Leaflet Marker!')
                .openPopup();

            // 5. Cleanup function: Destroys the map instance when the component unmounts
            return () => {
                map.remove();
                mapInstanceRef.current = null;
            };
        }
    }, []); // Empty dependency array means this runs only once on mount

    // 6. The component must return a JSX element (the container for the map)
    return (
        <div 
            ref={mapContainerRef} 
            id="map" 
            style={{ height: '500px', width: '100%' }} // Add necessary styling for the map to appear
        />
    );
}

export default Map;
